import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ResourceService } from '../services/resource.service';
import { FormGroup, FormControl } from '@angular/forms';

@Component({
  selector: 'app-todo-details',
  templateUrl: './todo-details.component.html',
  styleUrls: ['./todo-details.component.css']
})
export class TodoDetailsComponent implements OnInit {
  todo: any;
  todoForm: FormGroup = new FormGroup({});
  updateSuccess: boolean = false;
  updateError: boolean = false;

  constructor(
    private route: ActivatedRoute,
    private resourceService: ResourceService
  ) { }

  ngOnInit(): void {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.fetchTodo(id);
    } else {
      console.error('ID is null');
    }
  }

  fetchTodo(id: string): void {
    this.resourceService.getTodo(id).subscribe(
      response => {
        this.todo = response.todo;
        this.todoForm = new FormGroup({
          title: new FormControl(this.todo.title),
          content: new FormControl(this.todo.content),
          completed: new FormControl(this.todo.completed)
        });
      },
      error => {
        console.error(error);
      }
    );
  }

  onSubmit(): void {
    this.resourceService.updateTodo(this.todo._id, this.todoForm.value).subscribe(
      response => {
        this.updateSuccess = true;
        this.updateError = false;
      },
      error => {
        this.updateError = true;
        this.updateSuccess = false;
        console.error(error);
      }
    );
  }
}
<div *ngIf="todo && todoForm" class="card mt-5" style="background-color:#A6C5D6; margin-top: 50px; margin-bottom: 50px; padding-top: 20px; padding-bottom: 20px; width: 50%; margin-left: auto; margin-right: auto; text-align: center;">
  <div class="card-body" >
    <form [formGroup]="todoForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="title">Title</label>
        <input
          type="text"
          id="title"
          class="form-control w-50 mb-3"
          formControlName="title"
        />
      </div>
      <div class="form-group">
        <label for="content">Content</label>
        <textarea
          id="content"
          class="form-control w-50 mb-3"
          rows="3"
          formControlName="content"
        ></textarea>
      </div>
      <div class="form-check mb-3">
        <input
          type="checkbox"
          id="completed"
          class="form-check-input"
          formControlName="completed"
        />
        <label class="form-check-label" for="completed">Completed</label>
      </div>
      <button type="submit" class="btn btn-primary">Update</button>
    </form>
    <div class="alert alert-success mt-3" role="alert" *ngIf="updateSuccess">
      Todo updated successfully!
    </div>
    <div class="alert alert-danger mt-3" role="alert" *ngIf="updateError">
      There was an error updating the Todo.
    </div>
  </div>
</div>
