import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ResourceService } from '../services/resource.service';

@Component({
  selector: 'app-add-todo',
  templateUrl: './add-todo.component.html',
  styleUrls: ['./add-todo.component.css']
})
export class AddTodoComponent implements OnInit {
  todoForm: FormGroup;
  successMessage: string = '';
  errorMessage: string = '';
  updateSuccess: boolean = false;
  updateError: boolean = false;

  constructor(
    private fb: FormBuilder,
    private resourceService: ResourceService
  ) {
    this.todoForm = this.fb.group({
      title: ['', Validators.required],
      content: ['', Validators.required],
      completed: [false]
    });
  }

  ngOnInit(): void {}

  onSubmit(): void {
    if (this.todoForm.valid) {
      this.resourceService.createTodo(this.todoForm.value).subscribe(
        response => {
          this.updateSuccess = true;
          this.updateError = false;
          this.todoForm.reset();
        },
        error => {
          this.updateSuccess = false;
          this.updateError = true;
        }
      );
    }
  }
}
<div class="container">
  <div class="row justify-content-center">
    <div class="col-md-6">
      <form
        [formGroup]="todoForm"
        (ngSubmit)="onSubmit()"
        class="needs-validation"
        novalidate
      >
        <div class="form-group">
          <label for="title">Title</label>
          <input
            type="text"
            id="title"
            class="form-control mb-3"
            formControlName="title"
            required
          />
        </div>

        <div class="form-group">
          <label for="content">Content</label>
          <textarea
            id="content"
            class="form-control mb-3"
            rows="3"
            formControlName="content"
            required
          ></textarea>
        </div>

        <button type="submit" class="btn btn-primary">Add Todo</button>
      </form>

      <div class="alert alert-success mt-3" role="alert" *ngIf="updateSuccess">
        Todo added successfully!
      </div>
      <div class="alert alert-danger mt-3" role="alert" *ngIf="updateError">
        There was an error adding the Todo.
      </div>
    </div>
  </div>
</div>
